import React, { Component } from 'react';
import mainLogo from './iron-box.png';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import AppBar from '@material-ui/core/AppBar';
// import RaisedButton from 'material-ui/RaisedButton';
// import { MuiThemeProvider } from "@material-ui/core/styles";
// import moduleName from 'material-ui/styles/MuiThemeProvider';
import { Link,Redirect,Route } from 'react-router-dom';
import notFound from './notFound';
import NewUserLogin from './newUserLogin';


class Login1 extends Component {
    state = {
        details:{
        email:"",
        password:""
        }
      }

      onChange=e=>{
          const details = {...this.state.details}
          details[e.currentTarget.name] = e.currentTarget.value.trim();
          this.setState({ details });
          console.log(this.state);
      }

      onSubmit=e=>{
          e.preventDefault();
        //   console.log(this.state);
      }

    render() {
        return ( 

            <React.Fragment>


             <h1 style={{color:'red', marginBottom:'40%',textAlign:'center'}}>Login component -- 1</h1>
             <img  src={mainLogo} style={{width:'8%',marginRight:'90%',marginBottom:'30%'}}/>
           <div className="App">
                <header className='App-header'>
                    <form onSubmit={this.onChange} className='Login1'>
                     <div>    
                        <TextField     
                            type='text'
                            id='email'
                            placeholder='Enter Email ID'
                            style={{width:'110%',marginBottom:'-20%',margin:5,}}
                            value={this.state.details.email}
                            onChange={this.onChange}
                            name='email'
                        />
                        </div>

                        <br/>


                        <br/>


                    <div>
                        <TextField 
                            type='password'
                            id='password'
                            placeholder='Enter Password'
                            style={{width:'110%',margin:'9%',marginLeft:'2.9%'}}
                            value={this.state.details.password}
                            onChange={this.onChange}
                            name='password'
                        />
                        </div>        
                </form>
                </header>
                </div>

                <div className="attris" style={{justifyContent:'center',textAlign:'center',margin:'0%'}}>
                <Link to="/ForgotPassword" >
                 <a  style={{color:'lightcoral',fontSize:'100%',margin:1}}>Forgot Password ?</a>
                </Link>
                    <br/>
                    <br/>
                    <Link to="/new-User">
                    <a  style={{color:'lightcoral',fontSize:'100%',margin:-4}}>New Users SIGN-UP here?</a>
                    </Link>
                    <br/>
                    <Link to="/dashBoard">
                    <Button variant='contained' color='secondary' type='submit' onClick={this.onChange} style={{width:'-5%'}}>
                    Sign In 
                    </Button>
                    </Link>
                </div>

            </React.Fragment>

         );
    }
}

export default Login1;
