import React, { Component } from 'react';
import mainLogo from './iron-box.png';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';


class Login1 extends Component {
    state = {
        details:{
        email:"",
        password:""
        }
      }

      onChange=e=>{
          const details = {...this.state.details}
          details[e.currentTarget.name] = e.currentTarget.value;
          this.setState({ details });
      }

      onSubmit=e=>{
          e.preventDefault();
      }




    render() {

        const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
}));



        const classes = useStyles();

        return ( 
            <div>
                {/* <h1>Login component - 1</h1> */}
                <div className={classes.root}>
      <AppBar position="static">
        <Toolbar>
          <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" className={classes.title}>
            News
          </Typography>
          <Button color="inherit">Login</Button>
        </Toolbar>
      </AppBar>
    </div>
                <img  src={mainLogo} style={{width:'20%',marginRight:'90%',marginBottom:'30%'}}/>
                <div className='Login1'>
                    <form>
                    <div>
                        <TextField 
                            type='text'
                            id='email'
                            placeholder='Enter Email ID'
                            style={{width:'40%'}}
                            value={this.state.details.email}
                            onChange={this.onChange}
                            name='email'
                        />
                        </div>
                        <br/>
                    <div>
                        <TextField 
                            type='password'
                            id='password'
                            placeholder='Enter Password'
                            style={{width:'40%'}}
                            value={this.state.details.password}
                            onChange={this.onChange}
                            name='password'
                        />
                        </div>
                    </form>
                    <br/>
                    <Button variant='contained' color='secondary' type='submit' onClick={this.onSubmit}>Submit</Button>
                </div>

            </div>
         );
    }
}

export default Login1;
