        return ( 

            <div>

            <h1 style={Form}>Login component -- 1</h1>
             <img  src={mainLogo} style={{backgroundColor:'lightcoral',width:'8%'}}/>

{/* RESUABLE INPUTS */}

            <ReUse 
                type='text'
                name='email'
                placeholder='enter email'
                style={Form}
            />
            <br/>
            <ReUse 
                type='password'
                name='password'
                placeholder='enter email'
                style={Form}
            />


           {/* Route to the forms */}


                <Link to="/ForgotPassword" >
                 <a  style={Form}>Forgot Password ?</a>
                </Link>
                    <br/>
                    <br/>
                    <Link to="/new-User">
                    <a  style={Form}>New Users SIGN-UP here?</a>
                    </Link>
                    <br/>
                    <Link to="/dashBoard">
                    <Button variant='contained' color='secondary' type='submit' onClick={this.onChange} style={Form}>
                    Sign In 
                    </Button>
                    </Link>



            </div>
         );
    }
}

export default Login1;



