import React, { Component } from 'react';
import mainLogo from './iron-box.png';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import AppBar from '@material-ui/core/AppBar';
// import RaisedButton from 'material-ui/RaisedButton';
// import { MuiThemeProvider } from "@material-ui/core/styles";
// import moduleName from 'material-ui/styles/MuiThemeProvider';
import { Link,Redirect,Route } from 'react-router-dom';
import notFound from './notFound';
import NewUserLogin from './newUserLogin';
import ReUseInput from './ReUse';

// Styling Area
const Form = {
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          marginTop:0,
          marginBottom:0,
          margin:3    
}

const Inputs = {
          display:'flex',
          justifyContent: "center",
          alignItems: "center",
          marginTop:0,
          marginBottom:0           
}

const Alert = {
    fontSize:'10',
    color:'red',
    textAlign:'center'
}

const initialState = {
    email:'',
    password:'',
    emailError:'',
    passwordError:''
}

class Login1 extends Component {
    state = initialState;

      onChange=e=>{
         const isCheckbox = e.target.type === 'checkbox';
         this.setState({
             [e.target.name] : isCheckbox ?
             e.target.checked : e.target.value 
         });
      }

      validate = () => {
          let emailError="";
          let passwordError="";

        //   if (!this.state.email){
        //       emailError="email must be filled";
        //   }

        if (this.state.password){
            return <Link to="/dashBoard"/>
        }



          if(!this.state.email.includes("@")){
              emailError="Invalid email Id"
          }

          if(emailError || passwordError){
              this.setState({emailError,passwordError});
              return false;
          }

          return true;
    };

      onSubmit=e=>{
          e.preventDefault();
          const isValid = this.validate();
          if (isValid){
              console.log(this.state);
            //   clear
            this.setState(initialState);
          }

      };

    render() {
        return ( 

            <div>

            <h1 style={Form}>Login component -- 1</h1>
             <img  src={mainLogo} style={{backgroundColor:'lightcoral',width:'8%'}}/>

{/* RESUABLE INPUTS */}
    <form onSubmit={this.onSubmit}>

            <ReUseInput 
                type='text'
                name='email'
                placeholder='enter email'
                style={Inputs}
            />
            <div style={Alert}>{this.state.emailError}</div>
            <br/>
            <ReUseInput 
                type='password'
                name='password'
                placeholder='enter password'
                style={Inputs}
            />
            <div style={Alert}>{this.state.passwordError}</div>

<br/>

{/* <Link to="/dashBoard"> */}
<Button variant='contained' color='secondary' type='submit' style={Form}>
                    Sign In 
                    </Button>
{/* </Link> */}

    </form>


           {/* Route to the forms */}



                <Link to="/ForgotPassword" >
                 <a  style={Form}>Forgot Password ?</a>
                </Link>
                    <br/>
                    <br/>
                    <Link to="/new-User">
                    <a  style={Form}>New Users SIGN-UP here?</a>
                    </Link>
                    <br/>



            </div>
         );
    }
}

export default Login1;



