import React, { Component } from 'react';
import mainLogo from './iron-box.png';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import AppBar from '@material-ui/core/AppBar';
// import RaisedButton from 'material-ui/RaisedButton';
// import { MuiThemeProvider } from "@material-ui/core/styles";
// import moduleName from 'material-ui/styles/MuiThemeProvider';
import { Link } from 'react-router-dom';
// import notFound from './notFound';


class Login1 extends Component {
    state = {
        details:{
        email:"",
        password:""
        }
      }


      onChange=e=>{
          const details = {...this.state.details}
          details[e.currentTarget.name] = e.currentTarget.value.trim();
          this.setState({ details });
          console.log(this.state);
      }

      onSubmit=e=>{
          e.preventDefault();
        //   console.log(this.state);
      }




    render() {
        return ( 

            <React.Fragment>
             <h1 style={{color:'red', marginBottom:'40%',display:'flex'}}>Login component -- 1</h1>

           <div style={{position:'absolute'}}>
           <header className="App-header">
                <img  src={mainLogo} style={{width:'20%',marginRight:'90%',marginBottom:'30%'}}/>
                <div className='Login1'>

                    <form onSubmit={this.onChange}>
                    <div>
                        <TextField 

                            type='text'
                            id='email'
                            placeholder='Enter Email ID'
                            style={{width:'50%'}}
                            value={this.state.details.email}
                            onChange={this.onChange}
                            name='email'
                        />
                        </div>
                        <br/>
                    <div>
                        <TextField 
                            type='password'
                            id='password'
                            placeholder='Enter Password'
                            style={{width:'50%'}}
                            value={this.state.details.password}
                            onChange={this.onChange}
                            name='password'
                        />
                        </div>

                    </form>
                    <a href='/' style={{color:'lightcoral',fontSize:'60%',margin:10}} onClick={this.onSubmit}>Forgot Password ?</a>
                    <br/>

                    <Button variant='contained' color='secondary' type='submit' onClick={this.onChange} style={{width:'40%',margin:15}}>Sign In</Button>

                </div>
                </header>
            </div>
            </React.Fragment>

         );
    }
}

export default Login1;
