import React, { Component } from 'react';
// import React from 'react';
import mainLogo from './iron-box.png';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel';



class ForgotPassword extends Component {
    state = { 
      details:{
        email:'',
        phonenumber:''
      }
    }

    onChange=e=>{
      const details = {...this.state.details}
      details[e.currentTarget.name] = e.currentTarget.value;
      this.setState({ details });
      console.log(this.state)
    }



    render() { 
        return ( 
            <React.Fragment>

<h1 style={{color:'red'}}>Forgot Password Component</h1>
<div className='radio'>
<FormControlLabel value="female" control={<Radio />} label="Email Address"/>
<FormControlLabel value="female" control={<Radio />} label="Phone Number" />
</div>

<div style={{textAlign:'center',margin:30}}>

<TextField 
  type='text'
  placeholder='Enter Email'
  id='email'
  value={this.state.details.email}
  onChange={this.onChange}
  name='email'
  style={{width:'20%'}}
/>
</div>


<Button variant='contained' color='secondary' style={{textAlign:'center',marginRight:'10%'}}>
  Submit
</Button>



            </React.Fragment>
         );
    }
}

export default ForgotPassword;
